import React, { useState } from "react";
import {View,Text,FormControl,Input,InputField,Button,ButtonText, FormControlError, FormControlHelper, FormControlHelperText, FormControlErrorText, AlertCircleIcon, FormControlErrorIcon, Spinner,} from "@gluestack-ui/themed";

import { useFormik } from "formik";

import {getAuth, createUserWithEmailAndPassword } from "firebase/auth"
import {useNavigation} from "@react-navigation/native"
import {screen} from "../../utils"
import {initialValues, validationSchema} from "./RegisterForm.data";
import Toast from "react-native-toast-message";


  



const Register = () => {

  const [usuarioHaIniciadoSesion, setUsuarioHaIniciadoSesion] = useState(false);

  const navigation = useNavigation();

  const formik = useFormik({
    initialValues: initialValues(),
    validationSchema: validationSchema(),
    validateOnChange: false,
    onSubmit: async (formValue) => {
      try {
        const auth = getAuth();
        await createUserWithEmailAndPassword(
          auth, 
          formValue.email, 
          formValue.password
          );
          navigation.navigate(screen.account.login);
          setUsuarioHaIniciadoSesion(true);
      } catch (error) {
        Toast.show({
          type: "error",
          position: "bottom",
          text1: "Registration error, try later"
        })
      }
    },
  });

  return (
    <View>
      <FormControl
        isInvalid={true}
        size={"lg"}
        isDisabled={false}
        isRequired={true}
        w={"$4/5"}
        justifyContent="center"
        marginTop={"$5"}
      >
        <Input borderRadius="$2xl" borderWidth="$0" h={"$12"}>
          <InputField
            type="text"
            placeholder="Email"
            backgroundColor="white"
            onChangeText={(text) => formik.setFieldValue("email", text)}
          />
        </Input>
        <FormControlError>
          <FormControlErrorText>{formik.errors.email}</FormControlErrorText>
        </FormControlError>
      </FormControl>

      <FormControl
        isInvalid={true}
        size={"lg"}
        isDisabled={false}
        isRequired={true}
        w={"$4/5"}
        marginTop={"$5"}
      >
        <Input borderRadius="$2xl" borderWidth="$0" h={"$12"}>
          <InputField
            type={"password"}
            placeholder="Password"
            backgroundColor="white"
            onChangeText={(text) => formik.setFieldValue("password", text)}
          />
        </Input>
        <FormControlError>
          <FormControlErrorText>{formik.errors.password}</FormControlErrorText>
        </FormControlError>
      </FormControl>

      <FormControl
        isInvalid={true}
        size={"lg"}
        isDisabled={false}
        isRequired={true}
        w={"$4/5"}
        marginTop={"$5"}
      >
        <Input borderRadius="$2xl" borderWidth="$0" h={"$12"}>
          <InputField
            type={"password"}
            placeholder="Password"
            backgroundColor="white"
            onChangeText={(text) =>
              formik.setFieldValue("repeatPassword", text)
            }
          />
        </Input>
        <FormControlError>
          <FormControlErrorText>{formik.errors.repeatPassword}</FormControlErrorText>
        </FormControlError>
      </FormControl>

      <Button onPress={formik.handleSubmit}>
        <Text>Iniciar sesion</Text>
      </Button>
      

    </View>
  );
};
export default Register;
